Ossn.register_callback('ossn','init','ossn_administrator_update_widget');Ossn.register_callback('ossn','init','ossn_component_delete_confirmation');function ossn_administrator_update_widget(){$(document).ready(function(){if($('.avaiable-updates').length){Ossn.PostRequest({url:Ossn.site_url+"administrator/version",action:false,callback:function(callback){if(callback['version']){$('.avaiable-updates').html(callback['version']);}}});}});}
function ossn_component_delete_confirmation(){$(document).ready(function(){$('body').on('click','.ossn-component-delete-confirm',function(e){e.preventDefault();var actionurl=$(this).attr('href');var del=confirm(Ossn.Print('ossn:exception:make:sure'));if(del==true){var keep_pref=confirm(Ossn.Print('com:pref'));var keep='';if(keep_pref){keep='&keep_pref=1';}
window.location=actionurl+''+keep;}});});}Ossn.register_callback('ossn','init','ossn_profile_birthdate_picker');function ossn_profile_birthdate_picker(){$(document).ready(function(){var cYear=(new Date).getFullYear();var alldays=Ossn.Print('datepicker:days');var shortdays=alldays.split(",");var allmonths=Ossn.Print('datepicker:months');var shortmonths=allmonths.split(",");var datepick_args={changeMonth:true,changeYear:true,dateFormat:'dd/mm/yy',yearRange:'1900:'+cYear,};if(Ossn.isLangString('datepicker:days')){datepick_args['dayNamesMin']=shortdays;}
if(Ossn.isLangString('datepicker:months')){datepick_args['monthNamesShort']=shortmonths;}
var args=Ossn.call_hook('profile','birthdate:input',null,datepick_args);$("input[name='birthdate']").datepicker(args);$('.ui-datepicker').addClass('notranslate');});}